`user script`;function buildURL(href){if(!href.startsWith("http")){href="https://www.bdys10.com"+href}return href}function buildMedias(inputURL){const req={url:inputURL,method:"GET",};let datas=[];$http.fetch(req).then(res=>{const content=tXml.getElementsByClassName(res.body,"card card-sm card-link");content.forEach(dom=>{_len=dom.children.length;let href=findAllByKey(dom,"href")[0];const title=dom.children[_len-1].children[0].children[0];const coverURLString=findAllByKey(dom,"src")[0];let descriptionText=dom.children[_len-1].children[1].children[0];href=buildURL(href);datas.push(buildMediaData(href,coverURLString,title,descriptionText,href))});$next.toMedias(JSON.stringify(datas))})}function Episodes(inputURL){const req={url:inputURL,method:"GET",};let datas=[];$http.fetch(req).then(res=>{const content=tXml.getElementsByClassName(res.body,"btn btn-square me-2");content.forEach(element=>{let href=element.attributes.href;const title=element.children[0];href=buildURL(href);datas.push(buildEpisodeData(href,title,href))});$next.toEpisodes(JSON.stringify(datas))})}function Search(inputURL){}