`user script`;function buildMedias(inputURL){const q=inputURL.split("-");let url="https://api.live.bilibili.com/xlive/web-interface/v1/second/getUserRecommend?page="+q[0]+"&page_size=20&platform=web";if(q.length>1){url="https://api.live.bilibili.com/xlive/web-interface/v1/second/getList?platform=web&parent_area_id="+q[1]+"&sort_type=online&page="+q[0];if(q.length>2){url+="&area_id="+q[2]}}const req={url:url,method:"GET",};$http.fetch(req).then(res=>{const items=JSON.parse(res.body).data.list;var datas=[];items.forEach(item=>{const title=item.uname;const href=String(item.roomid);const coverURLString=item.cover;var descriptionText=item.title;datas.push(buildMediaData(href,coverURLString,title,descriptionText,href))});$next.toMedias(JSON.stringify(datas))})}function Episodes(inputURL){let req={url:"https://api.live.bilibili.com/xlive/web-room/v2/index/getRoomPlayInfo?room_id="+inputURL+"&protocol=0,1&format=0,1,2&codec=0,1&qn=30000&platform=web&ptype=8&dolby=5&panorama=1",method:"GET",};$http.fetch(req).then(res=>{const _json=JSON.parse(res.body).data.playurl_info.playurl.stream[1].format[1].codec[1];const playURL=_json.url_info[1].host+_json.base_url+_json.url_info[1].extra;let datas=[];datas.push(buildEpisodeData("real","原畫",playURL));$next.toEpisodes(JSON.stringify(datas))})}function Player(inputURL){$next.toPlayer(inputURL)}function Search(inputURL){}